mapper.update:

	update board 
       set hit=hit+1 
 	 where no=#{boardNo }


	update board 
	   set title=#{title }, contents=#{content }, reg_date=sysdate() 
	 where no=#{no }


	update board 
	   set order_no=order_no+1 
	 where group_no=#{groupNo}
	   and order_no>=#{orderNo}

update board
set parent_no=-1
where parent_no=#{no }
